// SPDX-License-Identifier: UNLICENSED
pragma solidity ^0.8.30;

import 'openzeppelin-foundry-upgrades/Upgrades.sol';
import 'forge-std/Script.sol';
import '../../src/Castora.sol';
import '../../src/CastoraActivities.sol';
import '../../src/CastoraGetters.sol';
import '../../src/CastoraPoolsManager.sol';
import '../../src/CastoraPoolsRules.sol';

contract SetupContracts is Script {
  CastoraActivities public activities = CastoraActivities(0x83d063ACDe4C3E799F0F0162d36D8b0605081b6e);
  Castora public castora = Castora(payable(0x9E1e6f277dF3f2cD150Ae1E08b05f45B3297bE6D));
  CastoraGetters public getters = CastoraGetters(0xf08959E66614027AE76303F4C5359eBfFd00Bc30);
  CastoraPoolsManager public poolsManager = CastoraPoolsManager(payable(0xF8f179Ab96165b61833F2930309bCE9c6aB281bE));
  CastoraPoolsRules public poolsRules = CastoraPoolsRules(0xfacA692BfeaFB4c6DCaF95a25E5CBCDB65d6eC41);
  address public admin;

  function run() public {
    // Load environment variables
    admin = vm.envAddress('ADMIN_ADDRESS');
    vm.startBroadcast(vm.envUint('CASTORA_OWNER_KEY'));

    // Setup contract relationships
    console.log('Setting up contract relationships...');

    // Set Castora address in PoolsManager
    poolsManager.setCastora(address(castora));

    // Authorize Castora and PoolsManager to log activities
    activities.setAuthorizedLogger(address(castora), true);
    activities.setAuthorizedLogger(address(poolsManager), true);

    // Grant admin roles
    castora.grantAdminRole(admin);
    castora.grantAdminRole(address(poolsManager));

    console.log('All contracts relationships have been set up successfully.');

    vm.stopBroadcast();
  }
}

src/Castora.sol.content:
// SPDX-License-Identifier: UNLICENSED
pragma solidity 0.8.30;

import {IERC20} from '@openzeppelin/contracts/token/ERC20/IERC20.sol';
import {SafeERC20} from '@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol';
import {AccessControlUpgradeable} from '@openzeppelin/contracts-upgradeable/access/AccessControlUpgradeable.sol';
import {OwnableUpgradeable} from '@openzeppelin/contracts-upgradeable/access/OwnableUpgradeable.sol';
import {Initializable} from '@openzeppelin/contracts-upgradeable/proxy/utils/Initializable.sol';
import {PausableUpgradeable} from '@openzeppelin/contracts-upgradeable/utils/PausableUpgradeable.sol';
import {ReentrancyGuardUpgradeable} from '@openzeppelin/contracts-upgradeable/utils/ReentrancyGuardUpgradeable.sol';
import {UUPSUpgradeable} from '@openzeppelin/contracts-upgradeable/proxy/utils/UUPSUpgradeable.sol';
import {CastoraActivities} from './CastoraActivities.sol';
import {CastoraEvents} from './CastoraEvents.sol';
import {CastoraPoolsManager} from './CastoraPoolsManager.sol';
import {CastoraPoolsRules} from './CastoraPoolsRules.sol';
import {CastoraState} from './CastoraState.sol';

/// Core prediction gaming contract where users stake tokens to predict future prices.
/// Participants compete by predicting token prices at specific future timestamps.
/// Winners are determined by accuracy and share the total staked funds proportionally.
contract Castora is
  CastoraEvents,
  CastoraState,
  Initializable,
  OwnableUpgradeable,
  AccessControlUpgradeable,
  ReentrancyGuardUpgradeable,
  PausableUpgradeable,
  UUPSUpgradeable
{
  using SafeERC20 for IERC20;

  /// Retrieves pool data by ID
  /// @param poolId ID of the pool to retrieve
  /// @return pool Complete pool data structure
  function getPool(uint256 poolId) external view returns (Pool memory pool) {
    if (poolId == 0 || poolId > allStats.noOfPools) revert InvalidPoolId();
    pool = pools[poolId];
  }

  /// Generates a unique hash for pool seeds to identify duplicate pools
  /// @param seeds Pool configuration parameters
  /// @return Hash of the pool seeds for uniqueness checking
  function hashPoolSeeds(PoolSeeds memory seeds) public pure returns (bytes32) {
    return keccak256(
      abi.encodePacked(
        seeds.predictionToken,
        seeds.stakeToken,
        seeds.stakeAmount,
        seeds.windowCloseTime,
        seeds.snapshotTime,
        seeds.multiplier
      )
    );
  }

  /// Generates a unique hash for user prediction records
  /// @param record User prediction record data
  /// @return hash of the record for tracking and indexing
  function hashPredictionRecord(PredictionRecord memory record) public pure returns (bytes32) {
    return keccak256(abi.encodePacked('poolId', record.poolId, 'predictionId', record.predictionId));
  }

  /// @custom:oz-upgrades-unsafe-allow constructor
  constructor() {
    _disableInitializers();
  }

  /// Initializes the contract with required dependency addresses and sets up roles
  /// @param activities_ Address of the CastoraActivities contract for activity logging
  /// @param poolsManager_ Address of the CastoraPoolsManager contract for pool management
  /// @param poolsRules_ Address of the CastoraPoolsRules contract for validation rules
  function initialize(address activities_, address poolsManager_, address poolsRules_) public initializer {
    if (activities_ == address(0)) revert InvalidAddress();
    if (poolsManager_ == address(0)) revert InvalidAddress();
    if (poolsRules_ == address(0)) revert InvalidAddress();

    activities = activities_;
    poolsManager = poolsManager_;
    poolsRules = poolsRules_;

    __Ownable_init(msg.sender);
    __AccessControl_init();
    __ReentrancyGuard_init();
    __Pausable_init();
    __UUPSUpgradeable_init();

    _grantRole(DEFAULT_ADMIN_ROLE, owner());
    _grantRole(ADMIN_ROLE, owner());
  }

  /// Authorizes contract upgrades restricted to owner
  /// @param newImpl Address of the new implementation contract
  function _authorizeUpgrade(address newImpl) internal override onlyOwner {}

  /// Pauses contract operations to prevent new interactions
  function pause() external onlyOwner nonReentrant whenNotPaused {
    _pause();
  }

  /// Unpauses contract operations to resume normal functionality
  function unpause() external onlyOwner nonReentrant whenPaused {
    _unpause();
  }

  /// Updates the CastoraActivities contract address for activity logging
  /// @param _activities New CastoraActivities contract address
  function setActivities(address _activities) external onlyOwner {
    if (_activities == address(0)) revert InvalidAddress();
    address oldActivities = activities;
    activities = _activities;
    emit SetActivitiesInCastora(oldActivities, _activities);
  }

  /// Sets the CastoraPoolsManager contract address
  /// @param _poolsManager The new CastoraPoolsManager contract address
  function setPoolsManager(address _poolsManager) external onlyOwner {
    if (_poolsManager == address(0)) revert InvalidAddress();
    address oldPoolsManager = poolsManager;
    poolsManager = _poolsManager;
    emit SetPoolsManagerInCastora(oldPoolsManager, _poolsManager);
  }

  /// Sets the CastoraPoolsRules contract address
  /// @param _poolsRules The new CastoraPoolsRules contract address
  function setPoolsRules(address _poolsRules) external onlyOwner {
    if (_poolsRules == address(0)) revert InvalidAddress();
    address oldPoolsRules = poolsRules;
    poolsRules = _poolsRules;
    emit SetPoolsRulesInCastora(oldPoolsRules, _poolsRules);
  }

  /// Grants admin role to enable pool creation and management functions
  /// @param admin Address to receive admin privileges
  function grantAdminRole(address admin) external onlyOwner {
    if (admin == address(0)) revert InvalidAddress();
    _grantRole(ADMIN_ROLE, admin);
  }

  /// Revokes admin role from an address
  /// @param admin Address to remove admin privileges from
  function revokeAdminRole(address admin) external onlyOwner {
    if (admin == address(0)) revert InvalidAddress();
    _revokeRole(ADMIN_ROLE, admin);
  }

  /// Creates a new prediction pool with specified parameters
  /// @param seeds Pool configuration including tokens, amounts, and timing
  /// @return poolId ID of the newly created pool
  function createPool(PoolSeeds memory seeds)
    external
    nonReentrant
    whenNotPaused
    onlyRole(ADMIN_ROLE)
    returns (uint256 poolId)
  {
    bytes32 seedsHash = hashPoolSeeds(seeds);
    if (poolIdsBySeedsHashes[seedsHash] != 0) revert PoolExistsAlready();

    CastoraPoolsRules(poolsRules).validateCreatePool(seeds);

    allStats.noOfPools += 1;
    poolIdsBySeedsHashes[seedsHash] = allStats.noOfPools;
    Pool storage pool = pools[allStats.noOfPools];
    pool.poolId = allStats.noOfPools;
    pool.seeds = seeds;
    pool.seedsHash = seedsHash;
    pool.creationTime = block.timestamp;

    if (predictionTokenDetails[seeds.predictionToken].noOfPools == 0) {
      allStats.noOfPredictionTokens += 1;
      predictionTokens.push(seeds.predictionToken);
    }
    predictionTokenDetails[seeds.predictionToken].noOfPools += 1;

    if (stakeTokenDetails[seeds.stakeToken].noOfPools == 0) {
      allStats.noOfStakeTokens += 1;
      stakeTokens.push(seeds.stakeToken);
    }
    stakeTokenDetails[seeds.stakeToken].noOfPools += 1;

    poolId = allStats.noOfPools;
    emit PoolCreated(poolId, seedsHash);
    CastoraActivities(activities).log(poolId, msg.sender, ActivityType.POOL_CREATED, poolId);
  }

  /// Validates pool exists and prediction window is still open
  /// @param poolId ID of the pool to validate
  /// @return pool Storage reference to the pool
  /// @return seeds Pool configuration parameters
  function _validateStartPredict(uint256 poolId) internal view returns (Pool storage pool, PoolSeeds memory seeds) {
    if (poolId == 0 || poolId > allStats.noOfPools) revert InvalidPoolId();
    pool = pools[poolId];
    seeds = pool.seeds;
    if (block.timestamp > seeds.windowCloseTime) revert WindowHasClosed();
  }

  /// Checks if this is a new user's first prediction and updates global user count
  /// @param poolId ID of the pool for activity logging
  function _checkNewUserOnPredict(uint256 poolId) internal {
    if (userStats[msg.sender].nthUserCount == 0) {
      allStats.noOfUsers += 1;
      userStats[msg.sender].nthUserCount = allStats.noOfUsers;
      users.push(msg.sender);
      emit NewUserPredicted(msg.sender, poolId, userStats[msg.sender].nthUserCount);
      CastoraActivities(activities).log(poolId, msg.sender, ActivityType.NEW_USER_ACTIVITY, allStats.noOfUsers);
    }
  }

  /// Updates global and user statistics when predictions are made
  /// @param poolId ID of the pool being predicted in
  /// @param predictionsCount Number of predictions being made
  /// @param seeds Pool configuration for token tracking
  function _updatePredictStatsGeneral(uint256 poolId, uint256 predictionsCount, PoolSeeds memory seeds) internal {
    if (userInPoolPredictionStats[poolId][msg.sender].noOfPredictions == 0) {
      userStats[msg.sender].noOfJoinedPools += 1;
      joinedPoolIdsByAddresses[msg.sender].push(poolId);
    }
    if (userPredictionTokenDetails[msg.sender][seeds.predictionToken].noOfPredictions == 0) {
      userPredictionTokens[msg.sender].push(seeds.predictionToken);
      userStats[msg.sender].noOfPredictionTokens += 1;
    }
    if (userStakeTokenDetails[msg.sender][seeds.stakeToken].noOfPredictions == 0) {
      userStakeTokens[msg.sender].push(seeds.stakeToken);
      userStats[msg.sender].noOfStakeTokens += 1;
    }

    allStats.noOfPredictions += predictionsCount;
    predictionTokenDetails[seeds.predictionToken].noOfPredictions += predictionsCount;
    stakeTokenDetails[seeds.stakeToken].noOfPredictions += predictionsCount;
    stakeTokenDetails[seeds.stakeToken].totalStaked += seeds.stakeAmount * predictionsCount;
    userStats[msg.sender].noOfPredictions += predictionsCount;
    userPredictionTokenDetails[msg.sender][seeds.predictionToken].noOfPredictions += predictionsCount;
    userStakeTokenDetails[msg.sender][seeds.stakeToken].noOfPredictions += predictionsCount;
    userStakeTokenDetails[msg.sender][seeds.stakeToken].totalStaked += seeds.stakeAmount * predictionsCount;
    userInPoolPredictionStats[poolId][msg.sender].noOfPredictions += predictionsCount;
  }

  /// Creates prediction record and updates activity tracking
  /// @param poolId ID of the pool containing the prediction
  /// @param predictionId Sequential ID for the new prediction
  /// @param predictionPrice Price prediction made by the user
  function _updatePredictStatsPrediction(uint256 poolId, uint256 predictionId, uint256 predictionPrice) internal {
    predictionIdsByAddressesPerPool[poolId][msg.sender].push(predictionId);

    PredictionRecord memory record = PredictionRecord(poolId, predictionId);
    bytes32 recordHash = hashPredictionRecord(record);
    predictionRecords[recordHash] = record;
    predictionRecordHashes.push(recordHash);
    userPredictionRecords[msg.sender].push(recordHash);

    predictions[poolId][predictionId] =
      Prediction(msg.sender, poolId, predictionId, predictionPrice, block.timestamp, 0, false);
    emit Predicted(poolId, predictionId, msg.sender, predictionPrice);
    CastoraActivities(activities).log(poolId, msg.sender, ActivityType.PREDICTED, allStats.noOfPredictions);
  }

  /// Makes a single prediction in a pool by staking the required amount
  /// @param poolId ID of the pool to predict in
  /// @param predictionPrice Predicted price for the token at snapshot time
  /// @return predictionId Sequential ID assigned to this prediction
  function predict(uint256 poolId, uint256 predictionPrice)
    external
    payable
    nonReentrant
    whenNotPaused
    returns (uint256 predictionId)
  {
    (Pool storage pool, PoolSeeds memory seeds) = _validateStartPredict(poolId);
    _checkNewUserOnPredict(poolId);
    _updatePredictStatsGeneral(poolId, 1, seeds);
    pool.noOfPredictions += 1;
    _updatePredictStatsPrediction(poolId, pool.noOfPredictions, predictionPrice);
    predictionId = pool.noOfPredictions;

    if (seeds.stakeToken == address(this)) {
      if (msg.value < seeds.stakeAmount) revert InsufficientStakeValue();
      if (msg.value > seeds.stakeAmount) revert IncorrectStakeValue();
    } else {
      IERC20(seeds.stakeToken).safeTransferFrom(msg.sender, address(this), seeds.stakeAmount);
    }
  }

  /// Makes multiple predictions with the same price in a single transaction
  /// @param poolId ID of the pool to predict in
  /// @param predictionPrice Predicted price to use for all predictions
  /// @param predictionsCount Number of predictions to make
  /// @return firstPredictionId ID of the first prediction made
  /// @return lastPredictionId ID of the last prediction made
  function bulkPredict(uint256 poolId, uint256 predictionPrice, uint16 predictionsCount)
    external
    payable
    nonReentrant
    whenNotPaused
    returns (uint256 firstPredictionId, uint256 lastPredictionId)
  {
    if (predictionsCount == 0) revert ZeroAmountSpecified();
    (Pool storage pool, PoolSeeds memory seeds) = _validateStartPredict(poolId);

    firstPredictionId = pool.noOfPredictions + 1;
    _checkNewUserOnPredict(poolId);
    _updatePredictStatsGeneral(poolId, predictionsCount, seeds);

    for (uint16 i = 0; i < predictionsCount; i += 1) {
      uint256 predictionId = firstPredictionId + i;
      _updatePredictStatsPrediction(poolId, predictionId, predictionPrice);
    }

    pool.noOfPredictions += predictionsCount;
    lastPredictionId = pool.noOfPredictions;

    if (seeds.stakeToken == address(this)) {
      if (msg.value < seeds.stakeAmount * predictionsCount) revert InsufficientStakeValue();
      if (msg.value > seeds.stakeAmount * predictionsCount) revert IncorrectStakeValue();
    } else {
      IERC20(seeds.stakeToken).safeTransferFrom(msg.sender, address(this), seeds.stakeAmount * predictionsCount);
    }
  }

  /// Begins pool completion process with actual price and batch size for processing
  /// @param poolId ID of the pool to complete
  /// @param snapshotPrice Actual price of the prediction token at snapshot time
  /// @param batchSize Number of winners to process per batch
  function initiatePoolCompletion(uint256 poolId, uint256 snapshotPrice, uint256 batchSize)
    external
    nonReentrant
    whenNotPaused
    onlyRole(ADMIN_ROLE)
  {
    if (poolId == 0 || poolId > allStats.noOfPools) revert InvalidPoolId();
    Pool storage pool = pools[poolId];
    if (pool.completionTime != 0) revert PoolAlreadyCompleted();
    if (hasPoolCompletionBeenInitiated[poolId]) revert PoolCompletionAlreadyInitiated();
    if (block.timestamp < pool.seeds.snapshotTime) revert NotYetSnapshotTime();
    if (pool.noOfPredictions == 0) revert NoPredictionsInPool();
    if (batchSize == 0) revert InvalidPoolCompletionBatchSize();
    // should never happen due to createPool validations, but worth having to prevent division by zero
    if (pool.seeds.multiplier == 0) revert InvalidPoolMultiplier();

    pool.snapshotPrice = snapshotPrice;

    uint256 noOfWinners;
    if (pool.noOfPredictions == 1) {
      noOfWinners = 1;
    } else {
      // the multiplier is in 2 decimal places (x2 is 200), so we multiply by 100 here to cancel it out
      // also rounding down is intended as solidity does that by default
      noOfWinners = (pool.noOfPredictions * 100) / pool.seeds.multiplier;
      if (noOfWinners == 0) noOfWinners = 1;
    }
    if (batchSize > noOfWinners) revert InvalidPoolCompletionBatchSize();
    pool.noOfWinners = noOfWinners;

    uint256 totalStaked = pool.seeds.stakeAmount * pool.noOfPredictions;
    uint256 fees = pool.seeds.feesPercent * totalStaked / 10000; // feesPercent is in 2 decimal places
    pool.winAmount = (totalStaked - fees) / noOfWinners;

    hasPoolCompletionBeenInitiated[poolId] = true;
    poolCompletionBatchSize[poolId] = batchSize;
    poolCompletionBatchesProcessed[poolId] = 0;
    emit PoolCompletionInitiated(poolId, noOfWinners, pool.winAmount);
    CastoraActivities(activities).log(poolId, msg.sender, ActivityType.POOL_COMPLETION_INITIATED, poolId);
  }

  /// Processes a batch of winning predictions and updates winner statistics
  /// @param poolId ID of the pool being completed
  /// @param winnerPredictionIds Array of prediction IDs that won in this batch
  function setWinnersInBatch(uint256 poolId, uint256[] memory winnerPredictionIds)
    external
    nonReentrant
    whenNotPaused
    onlyRole(ADMIN_ROLE)
  {
    if (poolId == 0 || poolId > allStats.noOfPools) revert InvalidPoolId();
    if (!hasPoolCompletionBeenInitiated[poolId]) revert PoolCompletionNotInitiated();

    Pool storage pool = pools[poolId];
    if (pool.completionTime != 0) revert PoolAlreadyCompleted();
    if (winnerPredictionIds.length == 0) revert InvalidPoolCompletionBatchSize();

    uint256 currentWinnersCount = winnerPredictionIds.length;
    uint256 batchSize = poolCompletionBatchSize[poolId];
    uint256 totalBatches = (pool.noOfWinners + batchSize - 1) / batchSize; // Ceiling division
    uint256 processedBatches = poolCompletionBatchesProcessed[poolId];
    if (processedBatches >= totalBatches) revert PoolCompletionBatchesAllProcessed();

    // Ensure correct batch size, last batch array length must match expected leftover count
    bool isLastBatch = totalBatches - processedBatches == 1;
    if (isLastBatch) {
      uint256 leftover = pool.noOfWinners - (processedBatches * batchSize);
      if (currentWinnersCount != leftover) revert InvalidPoolCompletionBatchSize();
    }
    // otherwise, array length must match expected batchSize
    if (!isLastBatch && currentWinnersCount != batchSize) revert InvalidPoolCompletionBatchSize();

    for (uint256 i = 0; i < currentWinnersCount; i++) {
      uint256 predictionId = winnerPredictionIds[i];
      if (predictionId == 0 || predictionId > pool.noOfPredictions) revert InvalidPredictionId();
      if (predictions[poolId][predictionId].isAWinner) revert PredictionAlreadyMarkedAsWinner(predictionId);

      // Mark prediction as winner
      predictions[poolId][predictionId].isAWinner = true;

      // Update all stats for this winner
      address predicter = predictions[poolId][predictionId].predicter;
      userStats[predicter].noOfWinnings += 1;
      userStats[predicter].noOfClaimableWinnings += 1;
      userStakeTokenDetails[predicter][pool.seeds.stakeToken].noOfWinnings += 1;
      userStakeTokenDetails[predicter][pool.seeds.stakeToken].noOfClaimableWinnings += 1;
      userStakeTokenDetails[predicter][pool.seeds.stakeToken].totalWon += pool.winAmount;
      userStakeTokenDetails[predicter][pool.seeds.stakeToken].totalClaimable += pool.winAmount;
      userInPoolPredictionStats[poolId][predicter].noOfWinnings += 1;
      userInPoolPredictionStats[poolId][predicter].noOfClaimableWinnings += 1;

      // Update tracking arrays
      winnerPredictionIdsByAddressesPerPool[poolId][predicter].push(predictionId);
      claimablePredictionIdsByAddressesPerPool[poolId][predicter].push(predictionId);
      bytes32 recordHash = hashPredictionRecord(PredictionRecord(poolId, predictionId));
      winnerRecordHashes.push(recordHash);
      winnerRecordHashesByAddresses[predicter].push(recordHash);
      claimableRecordHashesByAddresses[predicter].push(recordHash);
      claimableRecordHashesIndex[predicter][recordHash] = userStats[predicter].noOfClaimableWinnings - 1;
      claimablePredictionIdsInPoolIndex[poolId][predicter][predictionId] =
        userInPoolPredictionStats[poolId][predicter].noOfClaimableWinnings - 1;
    }

    allStats.noOfWinnings += currentWinnersCount;
    allStats.noOfClaimableWinnings += currentWinnersCount;
    stakeTokenDetails[pool.seeds.stakeToken].noOfWinnings += currentWinnersCount;
    stakeTokenDetails[pool.seeds.stakeToken].noOfClaimableWinnings += currentWinnersCount;
    stakeTokenDetails[pool.seeds.stakeToken].totalWon += pool.winAmount * currentWinnersCount;
    stakeTokenDetails[pool.seeds.stakeToken].totalClaimable += pool.winAmount * currentWinnersCount;
    poolCompletionBatchesProcessed[poolId] += 1;

    emit SetWinnersInBatch(poolId, poolCompletionBatchesProcessed[poolId], totalBatches, winnerPredictionIds.length);
  }

  /// Completes pool finalization after all winner batches are processed
  /// @param poolId ID of the pool to finalize
  function finalizePoolCompletion(uint256 poolId) external nonReentrant whenNotPaused onlyRole(ADMIN_ROLE) {
    if (poolId == 0 || poolId > allStats.noOfPools) revert InvalidPoolId();
    if (!hasPoolCompletionBeenInitiated[poolId]) revert PoolCompletionNotInitiated();

    Pool storage pool = pools[poolId];
    if (pool.completionTime != 0) revert PoolAlreadyCompleted();
    uint256 batchSize = poolCompletionBatchSize[poolId];
    uint256 totalBatches = (pool.noOfWinners + batchSize - 1) / batchSize; // Ceiling division
    if (poolCompletionBatchesProcessed[poolId] < totalBatches) revert PoolCompletionBatchesNotAllProcessed();

    pool.completionTime = block.timestamp;
    emit PoolCompleted(poolId);
    CastoraActivities(activities).log(poolId, msg.sender, ActivityType.POOL_COMPLETED, poolId);

    uint256 totalStaked = pool.seeds.stakeAmount * pool.noOfPredictions;
    uint256 fees = pool.seeds.feesPercent * totalStaked / 10000; // feesPercent is in 2 decimal places
    if (fees > 0) {
      if (pool.seeds.stakeToken == address(this)) {
        (bool isSuccess,) = payable(poolsManager).call{value: fees}('');
        if (!isSuccess) revert UnsuccessfulFeeCollection();
      } else {
        IERC20(pool.seeds.stakeToken).safeTransfer(poolsManager, fees);
      }

      CastoraPoolsManager(payable(poolsManager)).processPoolCompletion(poolId);
    }
  }

  /// Removes prediction record hash from user's claimable list efficiently using swap-and-pop
  /// @param recordHash Hash of the prediction record to remove from claimable list
  function _removeClaimableRecordHash(bytes32 recordHash) internal {
    uint256 indexToRemove = claimableRecordHashesIndex[msg.sender][recordHash];
    uint256 lastIndex = userStats[msg.sender].noOfClaimableWinnings - 1;

    if (indexToRemove != lastIndex) {
      bytes32 lastHash = claimableRecordHashesByAddresses[msg.sender][lastIndex];
      claimableRecordHashesByAddresses[msg.sender][indexToRemove] = lastHash;
      claimableRecordHashesIndex[msg.sender][lastHash] = indexToRemove;
    }

    claimableRecordHashesByAddresses[msg.sender].pop();
    delete claimableRecordHashesIndex[msg.sender][recordHash];
    userStats[msg.sender].noOfClaimableWinnings -= 1;
  }

  /// Removes prediction ID from user's claimable list in a specific pool
  /// @param poolId ID of the pool containing the prediction
  /// @param predictionId ID of the prediction to remove from claimable list
  function _removeClaimablePredictionIdInPool(uint256 poolId, uint256 predictionId) internal {
    uint256 indexToRemove = claimablePredictionIdsInPoolIndex[poolId][msg.sender][predictionId];
    uint256 lastIndex = userInPoolPredictionStats[poolId][msg.sender].noOfClaimableWinnings - 1;

    if (indexToRemove != lastIndex) {
      uint256 lastPredictionId = claimablePredictionIdsByAddressesPerPool[poolId][msg.sender][lastIndex];
      claimablePredictionIdsByAddressesPerPool[poolId][msg.sender][indexToRemove] = lastPredictionId;
      claimablePredictionIdsInPoolIndex[poolId][msg.sender][lastPredictionId] = indexToRemove;
    }

    claimablePredictionIdsByAddressesPerPool[poolId][msg.sender].pop();
    delete claimablePredictionIdsInPoolIndex[poolId][msg.sender][predictionId];
    userInPoolPredictionStats[poolId][msg.sender].noOfClaimableWinnings -= 1;
  }

  /// Updates all relevant statistics when winnings are claimed
  /// @param poolId ID of the pool where winnings were claimed
  /// @param winAmount Amount of tokens won
  /// @param stakeToken Address of the token being claimed
  function _updateClaimStats(uint256 poolId, uint256 winAmount, address stakeToken) internal {
    allStats.noOfClaimableWinnings -= 1;
    allStats.noOfClaimedWinnings += 1;
    userStats[msg.sender].noOfClaimedWinnings += 1;
    userInPoolPredictionStats[poolId][msg.sender].noOfClaimedWinnings += 1;
    stakeTokenDetails[stakeToken].noOfClaimableWinnings -= 1;
    stakeTokenDetails[stakeToken].noOfClaimedWinnings += 1;
    stakeTokenDetails[stakeToken].totalClaimable -= winAmount;
    stakeTokenDetails[stakeToken].totalClaimed += winAmount;
    userStakeTokenDetails[msg.sender][stakeToken].noOfClaimableWinnings -= 1;
    userStakeTokenDetails[msg.sender][stakeToken].noOfClaimedWinnings += 1;
    userStakeTokenDetails[msg.sender][stakeToken].totalClaimable -= winAmount;
    userStakeTokenDetails[msg.sender][stakeToken].totalClaimed += winAmount;
  }

  /// Internal logic for claiming winnings from a winning prediction
  /// @param poolId ID of the pool containing the winning prediction
  /// @param predictionId ID of the winning prediction to claim
  function _claimWinnings(uint256 poolId, uint256 predictionId) internal {
    if (poolId == 0 || poolId > allStats.noOfPools) revert InvalidPoolId();

    Pool storage pool = pools[poolId];
    if (pool.completionTime == 0) revert PoolNotYetCompleted();
    if (predictionId == 0 || predictionId > pool.noOfPredictions) revert InvalidPredictionId();

    Prediction storage prediction = predictions[poolId][predictionId];
    if (prediction.predicter != msg.sender) revert NotYourPrediction();
    if (!prediction.isAWinner) revert NotAWinner();
    if (prediction.claimedWinningsTime != 0) revert AlreadyClaimedWinnings();

    pool.noOfClaimedWinnings += 1;
    prediction.claimedWinningsTime = block.timestamp;
    _updateClaimStats(poolId, pool.winAmount, pool.seeds.stakeToken);

    bytes32 recordHash = hashPredictionRecord(PredictionRecord(poolId, predictionId));
    claimedRecordHashes.push(recordHash);
    _removeClaimableRecordHash(recordHash);
    _removeClaimablePredictionIdInPool(poolId, predictionId);

    emit ClaimedWinnings(
      poolId, predictionId, prediction.predicter, pool.seeds.stakeToken, pool.seeds.stakeAmount, pool.winAmount
    );
    CastoraActivities(activities).log(poolId, msg.sender, ActivityType.CLAIMED_WINNINGS, allStats.noOfClaimedWinnings);

    if (pool.seeds.stakeToken == address(this)) {
      (bool isSuccess,) = payable(prediction.predicter).call{value: pool.winAmount}('');
      if (!isSuccess) revert UnsuccessfulSendWinnings();
    } else {
      IERC20(pool.seeds.stakeToken).safeTransfer(prediction.predicter, pool.winAmount);
    }
  }

  /// Claims winnings for a single winning prediction
  /// @param poolId ID of the pool containing the winning prediction
  /// @param predictionId ID of the winning prediction to claim
  function claimWinnings(uint256 poolId, uint256 predictionId) external nonReentrant whenNotPaused {
    _claimWinnings(poolId, predictionId);
  }

  /// Claims winnings for multiple predictions across multiple pools in one transaction
  /// @param poolIds Array of pool IDs containing winning predictions
  /// @param predictionIds Array of prediction IDs to claim (must match poolIds length)
  function claimWinningsBulk(uint256[] memory poolIds, uint256[] memory predictionIds)
    external
    nonReentrant
    whenNotPaused
  {
    if (poolIds.length != predictionIds.length) {
      revert UnmatchingPoolsAndPredictions();
    }

    for (uint256 i = 0; i < poolIds.length; i += 1) {
      _claimWinnings(poolIds[i], predictionIds[i]);
    }
  }
}

src/CastoraActivities.sol.content:
// SPDX-License-Identifier: UNLICENSED
pragma solidity 0.8.30;

import {OwnableUpgradeable} from '@openzeppelin/contracts-upgradeable/access/OwnableUpgradeable.sol';
import {Initializable} from '@openzeppelin/contracts-upgradeable/proxy/utils/Initializable.sol';
import {ReentrancyGuardUpgradeable} from '@openzeppelin/contracts-upgradeable/utils/ReentrancyGuardUpgradeable.sol';
import {UUPSUpgradeable} from '@openzeppelin/contracts-upgradeable/proxy/utils/UUPSUpgradeable.sol';
import {CastoraErrors} from './CastoraErrors.sol';
import {CastoraEvents} from './CastoraEvents.sol';
import {CastoraStructs} from './CastoraStructs.sol';
import {console} from 'forge-std/console.sol';

/// Tracks and logs all user activities across the Castora ecosystem in chronological order.
/// Provides unified activity history for predictions, pool creation, completions, and claims.
/// Maintains engagement metrics and cross-contract activity correlation.
contract CastoraActivities is
  CastoraErrors,
  CastoraEvents,
  CastoraStructs,
  Initializable,
  OwnableUpgradeable,
  UUPSUpgradeable,
  ReentrancyGuardUpgradeable
{
  /// Total number of activities logged
  uint256 public noOfActivities;
  /// Global array of all activities in chronological order
  CastoraActivity[] public globalActivities;
  /// Count of activities by type
  mapping(ActivityType => uint256) public noOfActivitiesByType;
  /// Activity IDs by type
  mapping(ActivityType => uint256[]) public activityIdsByType;
  /// Mapping of user addresses to their activity IDs
  mapping(address => uint256) public userActivityIdsCount;
  /// Mapping of user addresses to their activity IDs
  mapping(address => uint256[]) public userActivityIds;
  /// Count of activities per user per type
  mapping(address => mapping(ActivityType => uint256)) public userActivityIdsCountByType;
  /// User activity IDs organized by activity type
  mapping(address => mapping(ActivityType => uint256[])) public userActivityIdsByType;
  /// Authorized contracts that can log activities
  mapping(address => bool) public isAuthorizedLogger;
  /// User engagement tracking - first activity time
  mapping(address => uint256) public userFirstActivityTime;
  /// User engagement tracking - last activity time
  mapping(address => uint256) public userLastActivityTime;
  /// User engagement tracking - first activity ID
  mapping(address => uint256) public userFirstActivityId;

  /// @custom:oz-upgrades-unsafe-allow constructor
  constructor() {
    _disableInitializers();
  }

  /// Initializes the contract
  function initialize() public initializer {
    __Ownable_init(msg.sender);
    __UUPSUpgradeable_init();
    __ReentrancyGuard_init();
  }

  function _authorizeUpgrade(address newImplementation) internal override onlyOwner {}

  /// Internal function to update user activity times
  /// @param user Address of the user
  /// @param activityId ID of the current activity
  function _updateUserActivityTimes(address user, uint256 activityId) internal {
    if (userFirstActivityTime[user] == 0) {
      userFirstActivityTime[user] = block.timestamp;
      userFirstActivityId[user] = activityId;
    }
    userLastActivityTime[user] = block.timestamp;
  }

  /// Internal helper to paginate activity arrays
  /// @param activityIds Array of activity IDs to paginate
  /// @param total Total number of items in the array
  /// @param offset Starting index
  /// @param limit Maximum items to return
  /// @return paginatedActivityIds The paginated activityIds
  function _paginateActivityIds(uint256[] storage activityIds, uint256 total, uint256 offset, uint256 limit)
    internal
    view
    returns (uint256[] memory paginatedActivityIds)
  {
    if (offset >= total) return new uint256[](0);

    uint256 end = offset + limit > total ? total : offset + limit;
    uint256 length = end - offset;
    paginatedActivityIds = new uint256[](length);

    for (uint256 i = 0; i < length; i++) {
      paginatedActivityIds[i] = activityIds[offset + i];
    }
  }

  function _findFirstActivityAtOrAfterForGlobal(uint256 timestamp) internal view returns (uint256) {
    if (noOfActivities == 0) return 0;

    uint256 left = 0;
    uint256 right = noOfActivities;

    while (left < right) {
      uint256 mid = left + (right - left) / 2;
      if (globalActivities[mid].timestamp < timestamp) {
        left = mid + 1;
      } else {
        right = mid;
      }
    }

    return left;
  }

  function _findFirstActivityAtOrAfter(uint256 timestamp, uint256 total, uint256[] storage activityIds)
    internal
    view
    returns (uint256)
  {
    if (total == 0) return 0;

    uint256 left = 0;
    uint256 right = total;

    while (left < right) {
      uint256 mid = left + (right - left) / 2;
      if (globalActivities[activityIds[mid] - 1].timestamp < timestamp) {
        left = mid + 1;
      } else {
        right = mid;
      }
    }

    return left;
  }

  function _paginateActivityIdsByTimeRange(
    uint256 startTime,
    uint256 endTime,
    uint256 offset,
    uint256 limit,
    uint256 total,
    uint256[] storage activityIds
  ) internal view returns (uint256[] memory paginatedActivityIds) {
    if (startTime > endTime) revert InvalidTimeRange();
    if (offset >= total) return new uint256[](0);

    uint256 startIndex = _findFirstActivityAtOrAfter(startTime, total, activityIds) + offset;
    if (startIndex >= total) return new uint256[](0);
    uint256 collected = 0;

    uint256[] memory temp = new uint256[](limit);
    for (uint256 i = startIndex; i < total && collected < limit; i++) {
      if (globalActivities[activityIds[i] - 1].timestamp >= endTime) break;
      temp[collected] = activityIds[i];
      collected++;
    }

    if (collected == 0) return new uint256[](0);
    if (collected == limit) return temp;
    paginatedActivityIds = new uint256[](collected);
    for (uint256 j = 0; j < collected; j++) {
      paginatedActivityIds[j] = temp[j];
    }
  }

  /// Gets a single activity by ID
  /// @param activityId ID of the activity
  /// @return activity The activity data
  function getOne(uint256 activityId) external view returns (CastoraActivity memory activity) {
    if (activityId == 0 || activityId > noOfActivities) revert InvalidActivityId();
    return globalActivities[activityId - 1];
  }

  /// Gets multiple activities by IDs
  /// @param activityIds Array of activity IDs
  /// @return activities Array of activity data
  function getMany(uint256[] calldata activityIds) external view returns (CastoraActivity[] memory activities) {
    activities = new CastoraActivity[](activityIds.length);
    for (uint256 i = 0; i < activityIds.length; i++) {
      if (activityIds[i] == 0 || activityIds[i] > noOfActivities) revert InvalidActivityId();
      activities[i] = globalActivities[activityIds[i] - 1];
    }
  }

  /// Gets paginated global activities
  /// @param offset Starting index
  /// @param limit Maximum activities to return
  /// @return activities Array of activities
  function getPaginated(uint256 offset, uint256 limit) external view returns (CastoraActivity[] memory activities) {
    if (offset >= noOfActivities) return new CastoraActivity[](0);

    uint256 end = offset + limit > noOfActivities ? noOfActivities : offset + limit;
    uint256 length = end - offset;
    activities = new CastoraActivity[](length);

    for (uint256 i = 0; i < length; i++) {
      activities[i] = globalActivities[offset + i];
    }
  }

  /// Gets paginated activities by type
  /// @param activityType Type of activities to retrieve
  /// @param offset Starting index
  /// @param limit Maximum activities to return
  /// @return activities Array of activities
  function getByTypePaginated(ActivityType activityType, uint256 offset, uint256 limit)
    external
    view
    returns (CastoraActivity[] memory activities)
  {
    uint256[] memory ids =
      _paginateActivityIds(activityIdsByType[activityType], noOfActivitiesByType[activityType], offset, limit);
    activities = new CastoraActivity[](ids.length);
    for (uint256 i = 0; i < ids.length; i++) {
      activities[i] = globalActivities[ids[i] - 1];
    }
  }

  /// Gets paginated activities for a specific user
  /// @param user Address of the user
  /// @param offset Starting index
  /// @param limit Maximum activities to return
  /// @return activities Array of activities
  function getForUserPaginated(address user, uint256 offset, uint256 limit)
    external
    view
    returns (CastoraActivity[] memory activities)
  {
    if (user == address(0)) revert InvalidAddress();
    uint256[] memory ids = _paginateActivityIds(userActivityIds[user], userActivityIdsCount[user], offset, limit);
    activities = new CastoraActivity[](ids.length);
    for (uint256 i = 0; i < ids.length; i++) {
      activities[i] = globalActivities[ids[i] - 1];
    }
  }

  /// Gets paginated activities for a specific user by type
  /// @param user Address of the user
  /// @param activityType Type of activities to retrieve
  /// @param offset Starting index
  /// @param limit Maximum activities to return
  /// @return activities Array of activities
  function getForUserByTypePaginated(address user, ActivityType activityType, uint256 offset, uint256 limit)
    external
    view
    returns (CastoraActivity[] memory activities)
  {
    if (user == address(0)) revert InvalidAddress();
    uint256[] memory ids = _paginateActivityIds(
      userActivityIdsByType[user][activityType], userActivityIdsCountByType[user][activityType], offset, limit
    );
    activities = new CastoraActivity[](ids.length);
    for (uint256 i = 0; i < ids.length; i++) {
      activities[i] = globalActivities[ids[i] - 1];
    }
  }

  /// Gets the count of activities for a specific user by type
  /// @param user Address of the user
  /// @param activityType Type of activities to count
  /// @return count Number of activities
  function getActivityTypeCountForUser(address user, ActivityType activityType) external view returns (uint256 count) {
    if (user == address(0)) revert InvalidAddress();
    return userActivityIdsCountByType[user][activityType];
  }

  /// Gets activities within a time range with single pass
  /// @param startTime Start timestamp (inclusive)
  /// @param endTime End timestamp (exclusive)
  /// @param offset Number of matching activities to skip
  /// @param limit Maximum number of activities to return
  /// @return activities Array of activities within the time range
  function getByTimeRangePaginated(uint256 startTime, uint256 endTime, uint256 offset, uint256 limit)
    external
    view
    returns (CastoraActivity[] memory activities)
  {
    if (startTime > endTime) revert InvalidTimeRange();
    if (offset >= noOfActivities) return new CastoraActivity[](0);

    uint256 startIndex = _findFirstActivityAtOrAfterForGlobal(startTime) + offset;
    if (startIndex >= noOfActivities) return new CastoraActivity[](0);
    uint256 collected = 0;

    CastoraActivity[] memory tempActivities = new CastoraActivity[](limit);

    for (uint256 i = startIndex; i < noOfActivities && collected < limit; i++) {
      if (globalActivities[i].timestamp >= endTime) break;
      tempActivities[collected] = globalActivities[i];
      collected++;
    }

    if (collected == 0) return new CastoraActivity[](0);
    if (collected == limit) return tempActivities;
    activities = new CastoraActivity[](collected);
    for (uint256 j = 0; j < collected; j++) {
      activities[j] = tempActivities[j];
    }
  }

  /// Gets activities by type within a time range with single pass
  /// @param activityType Type of activities to retrieve
  /// @param startTime Start timestamp (inclusive)
  /// @param endTime End timestamp (exclusive)
  /// @param offset Number of matching activities to skip
  /// @param limit Maximum number of activities to return
  /// @return activities Array of activities within the time range
  function getByTypeByTimeRangePaginated(
    ActivityType activityType,
    uint256 startTime,
    uint256 endTime,
    uint256 offset,
    uint256 limit
  ) external view returns (CastoraActivity[] memory activities) {
    uint256[] memory ids = _paginateActivityIdsByTimeRange(
      startTime, endTime, offset, limit, noOfActivitiesByType[activityType], activityIdsByType[activityType]
    );
    activities = new CastoraActivity[](ids.length);
    for (uint256 i = 0; i < ids.length; i++) {
      activities[i] = globalActivities[ids[i] - 1];
    }
  }

  /// Gets user activities within a time range with single pass
  /// @param user Address of the user
  /// @param startTime Start timestamp (inclusive)
  /// @param endTime End timestamp (exclusive)
  /// @param offset Number of matching activities to skip
  /// @param limit Maximum number of activities to return
  /// @return activities Array of activities within the time range
  function getForUserByTimeRangePaginated(
    address user,
    uint256 startTime,
    uint256 endTime,
    uint256 offset,
    uint256 limit
  ) external view returns (CastoraActivity[] memory activities) {
    uint256[] memory ids = _paginateActivityIdsByTimeRange(
      startTime, endTime, offset, limit, userActivityIdsCount[user], userActivityIds[user]
    );
    activities = new CastoraActivity[](ids.length);
    for (uint256 i = 0; i < ids.length; i++) {
      activities[i] = globalActivities[ids[i] - 1];
    }
  }

  /// Gets user activities by type within a time range with single pass
  /// @param user Address of the user
  /// @param activityType Type of activities to retrieve
  /// @param startTime Start timestamp (inclusive)
  /// @param endTime End timestamp (exclusive)
  /// @param offset Number of matching activities to skip
  /// @param limit Maximum number of activities to return
  /// @return activities Array of activities within the time range
  function getForUserByTypeByTimeRangePaginated(
    address user,
    ActivityType activityType,
    uint256 startTime,
    uint256 endTime,
    uint256 offset,
    uint256 limit
  ) external view returns (CastoraActivity[] memory activities) {
    uint256[] memory ids = _paginateActivityIdsByTimeRange(
      startTime,
      endTime,
      offset,
      limit,
      userActivityIdsCountByType[user][activityType],
      userActivityIdsByType[user][activityType]
    );
    activities = new CastoraActivity[](ids.length);
    for (uint256 i = 0; i < ids.length; i++) {
      activities[i] = globalActivities[ids[i] - 1];
    }
  }

  /// Gets recent activities (last N activities)
  /// @param limit Maximum number of recent activities to return
  /// @return activities Array of recent activities
  function getRecentActivities(uint256 limit) external view returns (CastoraActivity[] memory activities) {
    if (limit > noOfActivities) limit = noOfActivities;
    if (limit == 0) return new CastoraActivity[](0);

    activities = new CastoraActivity[](limit);
    for (uint256 i = 0; i < limit; i++) {
      activities[i] = globalActivities[noOfActivities - 1 - i];
    }
  }

  function getRecentActivitiesByType(ActivityType activityType, uint256 limit)
    external
    view
    returns (CastoraActivity[] memory activities)
  {
    if (limit > noOfActivitiesByType[activityType]) limit = noOfActivitiesByType[activityType];
    if (limit == 0) return new CastoraActivity[](0);

    activities = new CastoraActivity[](limit);
    for (uint256 i = 0; i < limit; i++) {
      activities[i] = globalActivities[activityIdsByType[activityType][noOfActivitiesByType[activityType] - 1 - i] - 1];
    }
  }

  function getRecentActivitiesForUser(address user, uint256 limit)
    external
    view
    returns (CastoraActivity[] memory activities)
  {
    if (user == address(0)) revert InvalidAddress();
    if (limit > userActivityIdsCount[user]) limit = userActivityIdsCount[user];
    if (limit == 0) return new CastoraActivity[](0);

    activities = new CastoraActivity[](limit);
    for (uint256 i = 0; i < limit; i++) {
      activities[i] = globalActivities[userActivityIds[user][userActivityIdsCount[user] - 1 - i] - 1];
    }
  }

  function getRecentActivitiesForUserByType(address user, ActivityType activityType, uint256 limit)
    external
    view
    returns (CastoraActivity[] memory activities)
  {
    if (user == address(0)) revert InvalidAddress();
    if (limit > userActivityIdsCountByType[user][activityType]) limit = userActivityIdsCountByType[user][activityType];
    if (limit == 0) return new CastoraActivity[](0);

    activities = new CastoraActivity[](limit);
    for (uint256 i = 0; i < limit; i++) {
      activities[i] = globalActivities[userActivityIdsByType[user][activityType][userActivityIdsCountByType[user][activityType]
        - 1 - i] - 1];
    }
  }

  /// Sets authorization status for a contract
  /// @param contractAddr Address of the contract to authorize/deauthorize
  /// @param authorized Whether the contract should be authorized
  function setAuthorizedLogger(address contractAddr, bool authorized) external onlyOwner {
    if (contractAddr == address(0)) revert InvalidAddress();
    isAuthorizedLogger[contractAddr] = authorized;
    emit AuthorizedContractUpdated(contractAddr, authorized);
  }

  /// Logs a new activity (called by authorized contracts)
  /// @param poolId Related pool ID
  /// @param user Address of the user performing the activity
  /// @param activityType Type of activity being logged
  /// @param refGlobalCount Reference count in source contract
  function log(uint256 poolId, address user, ActivityType activityType, uint256 refGlobalCount) external nonReentrant {
    if (!isAuthorizedLogger[msg.sender]) revert UnauthorizedActivityLogger();
    if (poolId == 0) revert InvalidPoolId();
    if (user == address(0)) revert InvalidAddress();
    if (refGlobalCount == 0) revert InvalidActivityId();

    // Create activity
    CastoraActivity memory activity = CastoraActivity({
      timestamp: block.timestamp,
      poolId: poolId,
      user: user,
      activityType: activityType,
      sourceContract: msg.sender,
      refGlobalCount: refGlobalCount
    });

    // Update counters
    noOfActivities += 1;
    noOfActivitiesByType[activityType] += 1;
    userActivityIdsCount[user] += 1;
    userActivityIdsCountByType[user][activityType] += 1;

    // Store activity
    uint256 activityId = noOfActivities;
    globalActivities.push(activity);
    activityIdsByType[activityType].push(activityId);
    userActivityIds[user].push(activityId);
    userActivityIdsByType[user][activityType].push(activityId);

    // Update user engagement tracking
    _updateUserActivityTimes(user, activityId);
  }
}

src/CastoraErrors.sol.content:
// SPDX-License-Identifier: UNLICENSED
pragma solidity 0.8.30;

/// Defines all custom error types used across the Castora ecosystem.
/// Provides consistent error handling and gas-efficient reverts throughout the system.
contract CastoraErrors {
  error AlreadyClaimedCompletionFees();
  error AlreadyClaimedWinnings();
  error CastoraAddressNotSet();
  error CreationFeeTokenAlreadyDisallowed();
  error CreationFeeTokenNotAllowed();
  error IncorrectCreationFeeValue();
  error IncorrectStakeValue();
  error InsufficientCreationFeeValue();
  error InsufficientStakeValue();
  error InvalidAddress();
  error InvalidActivityId();
  error InvalidRecordHash();
  error InvalidPoolCompletionBatchSize();
  error InvalidPoolFeesPercent();
  error InvalidPoolId();
  error InvalidPoolTimes();
  error InvalidPoolTimeInterval();
  error InvalidPoolMultiplier();
  error InvalidPredictionId();
  error InvalidSplitFeesPercent();
  error InvalidTimeRange();
  error InvalidWinnersCount();
  error NoPredictionsInPool();
  error NotAWinner();
  error NotYetSnapshotTime();
  error NotYourPool();
  error NotYourPrediction();
  error PoolAlreadyCompleted();
  error PoolCompletionAlreadyInitiated();
  error PoolCompletionAlreadyProcessed();
  error PoolCompletionBatchesAllProcessed();
  error PoolCompletionBatchesNotAllProcessed();
  error PoolCompletionNotInitiated();
  error PoolExistsAlready();
  error PoolNotYetCompleted();
  error PredictionAlreadyMarkedAsWinner(uint256 predictionId);
  error PredictionTokenNotAllowed();
  error StakeTokenNotAllowed();
  error StakeAmountNotAllowed();
  error UnauthorizedActivityLogger();
  error UnsuccessfulCreationFeeCollection();
  error UnsuccessfulFeeCollection();
  error UnsuccessfulSendCompletionFees();
  error UnsuccessfulSendWinnings();
  error WindowHasClosed();
  error ZeroAmountSpecified();
  error UnmatchingPoolsAndPredictions();
}

src/CastoraEvents.sol.content:
// SPDX-License-Identifier: UNLICENSED
pragma solidity 0.8.30;

/// Defines all events emitted across the Castora ecosystem for comprehensive logging.
/// Enables efficient indexing and filtering of activities, state changes, and user interactions.
contract CastoraEvents {
  /// Emitted when a {Pool} is created with `poolId` and `seedsHash`.
  event PoolCreated(uint256 indexed poolId, bytes32 indexed seedsHash);

  /// Emitted when a user (with `predicter` address) interacts with Castora
  /// for the first time by joining a {Pool} with matching `poolId`.
  event NewUserPredicted(address indexed predicter, uint256 indexed poolId, uint256 indexed nthUserCount);

  /// Emitted when a participant (with `predicter` address) joins a {Pool}
  /// with matching `poolId` with the new `predictionId` for a
  /// `predictionPrice`.
  event Predicted(
    uint256 indexed poolId, uint256 indexed predictionId, address indexed predicter, uint256 predictionPrice
  );

  /// Emitted when the {Pool} with `poolId` finalizes its completion.
  event PoolCompleted(uint256 indexed poolId);

  /// Emitted when the address of predicter (now a `winner`) that made the
  /// {Prediction} with `predictionId` in a {Pool} with `poolId` claims
  /// the `awardedAmount` of the `stakeToken` for their initial `stakeAmount`.
  event ClaimedWinnings(
    uint256 indexed poolId,
    uint256 indexed predictionId,
    address indexed winner,
    address stakeToken,
    uint256 stakedAmount,
    uint256 wonAmount
  );

  /// Emitted when a stake token's allowed status is updated.
  event UpdatedAllowedStakeToken(address indexed token, bool allowed);

  /// Emitted when a prediction token's allowed status is updated.
  event UpdatedAllowedPredictionToken(address indexed token, bool allowed);

  /// Emitted when a specific stake amount's allowed status is updated for a token.
  event UpdatedAllowedStakeAmount(address indexed token, uint256 amount, bool allowed);

  /// Emitted when a specific multiplier's allowed status is updated for a pool.
  event UpdatedAllowedPoolMultiplier(uint16 multiplier, bool allowed);

  /// Emitted when the required time interval for pool timing validation is updated.
  event UpdatedRequiredTimeInterval(uint256 oldInterval, uint256 newInterval);

  /// Emitted when the current pool fees percentage from winnings is updated.
  event UpdatedCurrentPoolFeesPercent(uint16 oldPercent, uint16 newPercent);

  event SetActivitiesInCastora(address indexed oldActivities, address indexed newActivities);
  event SetActivitiesInPoolsManager(address indexed oldActivities, address indexed newActivities);

  /// Emitted when the Castora contract address is updated
  /// @param oldCastora The previous Castora contract address
  /// @param newCastora The new Castora contract address
  event SetCastoraInPoolsManager(address indexed oldCastora, address indexed newCastora);

  /// Emitted when the CastoraPoolsManager contract address is updated
  /// @param oldPoolsManager The previous CastoraPoolsManager contract address
  /// @param newPoolsManager The new CastoraPoolsManager contract address
  event SetPoolsManagerInCastora(address indexed oldPoolsManager, address indexed newPoolsManager);

  /// Emitted when the CastoraPoolsRules contract address is updated
  /// @param oldPoolsRules The previous CastoraPoolsRules contract address
  /// @param newPoolsRules The new CastoraPoolsRules contract address
  event SetPoolsRulesInCastora(address indexed oldPoolsRules, address indexed newPoolsRules);

  /// Emitted when the fee collector address is updated
  /// @param oldFeeCollector The previous fee collector address
  /// @param newFeeCollector The new fee collector address
  event SetFeeCollector(address indexed oldFeeCollector, address indexed newFeeCollector);

  /// Emitted when the completion pool fees split percentage is updated
  /// @param oldPercentage The previous split percentage
  /// @param newPercentage The new split percentage
  event SetCreatorPoolCompletionFeesSplitPercent(uint256 oldPercentage, uint256 newPercentage);

  /// Emitted when a token is allowed for creation fees
  /// @param token The token address that was allowed
  /// @param amount The fee amount set for this token
  event SetCreationFees(address indexed token, uint256 amount);

  /// Emitted when a token is disallowed for creation fees
  /// @param token The token address that was disallowed
  event DisallowedCreationFees(address indexed token);

  /// Emitted when a user (with `predicter` address) interacts with CastoraPoolsManager
  /// for the first time by creating a {Pool} with matching `poolId`.
  event NewUserCreatedPool(address indexed predicter, uint256 indexed poolId, uint256 indexed nthUserCount);

  /// Emitted when a pool is created
  /// @param poolId The ID of the created pool
  /// @param creator The address of the pool creator
  /// @param creationFeesToken The token used for creation fees
  /// @param creationFeesAmount The amount paid for creation fees
  event UserHasCreatedPool(
    uint256 indexed poolId, address indexed creator, address indexed creationFeesToken, uint256 creationFeesAmount
  );

  /// Emitted when a pool created by a user is completed and the claims are set
  /// @param poolId The ID of the completed pool
  /// @param completionFeesToken The token used for completion fees
  /// @param completionFeesAmount The users allocation they won
  event IssuedCompletionFees(uint256 indexed poolId, address indexed completionFeesToken, uint256 completionFeesAmount);

  /// Emitted when completion fees are claimed
  /// @param poolId The ID of the pool
  /// @param user The address claiming the fees
  /// @param token The token being claimed
  /// @param amount The amount being claimed
  event ClaimedCompletionFees(uint256 indexed poolId, address indexed user, address indexed token, uint256 amount);

  /// Emitted when the Pools Manager contract receives native token funds
  /// @param sender The address that sent the money
  /// @param amount The amount received
  event ReceiveWasCalled(address indexed sender, uint256 amount);

  /// Emitted when pool completion is initiated
  event PoolCompletionInitiated(uint256 indexed poolId, uint256 noOfWinners, uint256 winAmount);

  /// Emitted when a batch of winners is processed
  event SetWinnersInBatch(
    uint256 indexed poolId, uint256 batchesProcessed, uint256 totalBatches, uint256 winnersInBatch
  );

  /// Emitted when a contract's authorization status is updated
  event AuthorizedContractUpdated(address indexed contractAddr, bool authorized);
}

src/CastoraGetters.sol.content:
// SPDX-License-Identifier: UNLICENSED
pragma solidity 0.8.30;

import {CastoraErrors} from './CastoraErrors.sol';
import {CastoraState} from './CastoraState.sol';
import {CastoraStructs} from './CastoraStructs.sol';

/// Dedicated getter contract for improved reads from the main Castora contract.
/// Exposes paginated queries for users, pools, predictions, tokens, and other statistics.
contract CastoraGetters is CastoraErrors, CastoraStructs {
  /// Reference to the main Castora contract (that would have inherited CastoraState) for easy calls
  CastoraState public immutable state;

  /// Address of the main Castora contract
  address public immutable castora;

  /// Initializes getter contract with reference to main Castora contract
  /// @param castora_ Address of the deployed Castora contract
  constructor(address castora_) {
    if (castora_ == address(0)) revert InvalidAddress();
    castora = castora_;
    state = CastoraState(castora_);
  }

  /// Returns global statistics for all pools and predictions
  /// @return stats Struct containing aggregated protocol statistics
  function allStats() public view returns (AllPredictionStats memory stats) {
    (uint256 a, uint256 b, uint256 c, uint256 d, uint256 e, uint256 f, uint256 g, uint256 h) = state.allStats();
    stats = AllPredictionStats(a, b, c, d, e, f, g, h);
  }

  /// Paginates through address arrays to handle large datasets
  /// @param array Storage array to paginate
  /// @param total Total length of the array
  /// @param offset Starting index for pagination
  /// @param limit Maximum number of items to return
  /// @return items Slice of addresses from the array
  function _paginateAddressArray(
    function (uint256) external view returns (address) array,
    uint256 total,
    uint256 offset,
    uint256 limit
  ) internal view returns (address[] memory items) {
    if (offset >= total) return new address[](0);

    uint256 end = offset + limit > total ? total : offset + limit;
    uint256 length = end - offset;
    items = new address[](length);

    for (uint256 i = 0; i < length; i += 1) {
      items[i] = array(offset + i);
    }
  }

  /// Paginates the PredictionRecords of a given context
  /// @param array Storage array of the record hashes
  /// @param total Total length of the array
  /// @param offset Starting index for pagination
  /// @param limit Maximum number of items to return
  /// @return items Slice of PredictionRecords from the array
  function _paginatePredictionRecordsArray(
    function (uint256) external view returns (bytes32) array,
    uint256 total,
    uint256 offset,
    uint256 limit
  ) internal view returns (PredictionRecord[] memory items) {
    if (offset >= total) return new PredictionRecord[](0);

    uint256 end = offset + limit > total ? total : offset + limit;
    uint256 length = end - offset;
    items = new PredictionRecord[](length);

    for (uint256 i = 0; i < length; i += 1) {
      (uint256 poolId, uint256 predictionId) = state.predictionRecords(array(offset + i));
      items[i] = PredictionRecord(poolId, predictionId);
    }
  }

  /// Paginates the PredictionRecords of a user based on a given context
  /// @param array Storage array of the record hashes
  /// @param user Address of the involved user
  /// @param total Total length of the array
  /// @param offset Starting index for pagination
  /// @param limit Maximum number of items to return
  /// @return items Slice of addresses from the array
  function _paginateUserPredictionRecordsArray(
    function (address, uint256) external view returns (bytes32) array,
    address user,
    uint256 total,
    uint256 offset,
    uint256 limit
  ) internal view returns (PredictionRecord[] memory items) {
    if (offset >= total) return new PredictionRecord[](0);

    uint256 end = offset + limit > total ? total : offset + limit;
    uint256 length = end - offset;
    items = new PredictionRecord[](length);

    for (uint256 i = 0; i < length; i += 1) {
      (uint256 poolId, uint256 predictionId) = state.predictionRecords(array(user, offset + i));
      items[i] = PredictionRecord(poolId, predictionId);
    }
  }

  /// Paginates through uint256 arrays to handle large datasets
  /// @param array Storage array to paginate
  /// @param total Total length of the array
  /// @param offset Starting index for pagination
  /// @param limit Maximum number of items to return
  /// @return items Slice of uint256 values from the array
  function _paginateUint256ArrayForUserInPool(
    function (uint256, address, uint256) external view returns (uint256) array,
    uint256 poolId,
    address user,
    uint256 total,
    uint256 offset,
    uint256 limit
  ) internal view returns (uint256[] memory items) {
    if (offset >= total) return new uint256[](0);

    uint256 end = offset + limit > total ? total : offset + limit;
    uint256 length = end - offset;
    items = new uint256[](length);

    for (uint256 i = 0; i < length; i += 1) {
      items[i] = array(poolId, user, offset + i);
    }
  }

  /// Paginates through address arrays to handle large datasets per user
  /// @param array Storage array to paginate
  /// @param user The user whose arrays are being paginated
  /// @param total Total length of the array
  /// @param offset Starting index for pagination
  /// @param limit Maximum number of items to return
  /// @return items Slice of addresses from the array
  function _paginateAddressArrayForUser(
    function (address, uint256) external view returns (address) array,
    address user,
    uint256 total,
    uint256 offset,
    uint256 limit
  ) internal view returns (address[] memory items) {
    if (offset >= total) return new address[](0);

    uint256 end = offset + limit > total ? total : offset + limit;
    uint256 length = end - offset;
    items = new address[](length);

    for (uint256 i = 0; i < length; i += 1) {
      items[i] = array(user, offset + i);
    }
  }

  /// Returns paginated list of users who have interacted with the contract
  /// @param offset Starting index for pagination
  /// @param limit Maximum number of users to return
  /// @return usersList Array of user addresses
  function usersPaginated(uint256 offset, uint256 limit) external view returns (address[] memory usersList) {
    usersList = _paginateAddressArray(state.users, allStats().noOfUsers, offset, limit);
  }

  /// Returns paginated list of tokens used for predictions
  /// @param offset Starting index for pagination
  /// @param limit Maximum number of tokens to return
  /// @return tokensList Array of token addresses used in predictions
  function predictionTokensPaginated(uint256 offset, uint256 limit) external view returns (address[] memory tokensList) {
    tokensList = _paginateAddressArray(state.predictionTokens, allStats().noOfPredictionTokens, offset, limit);
  }

  /// Returns paginated list of tokens used for staking
  /// @param offset Starting index for pagination
  /// @param limit Maximum number of tokens to return
  /// @return tokensList Array of token addresses used for staking
  function stakeTokensPaginated(uint256 offset, uint256 limit) external view returns (address[] memory tokensList) {
    tokensList = _paginateAddressArray(state.stakeTokens, allStats().noOfStakeTokens, offset, limit);
  }

  /// Retrieves user prediction record details by hash
  /// @param recordHash Hash of the record to retrieve
  /// @return record User prediction record data
  function predictionRecord(bytes32 recordHash) external view returns (PredictionRecord memory record) {
    (uint256 poolId, uint256 predictionId) = state.predictionRecords(recordHash);
    if (poolId == 0) revert InvalidRecordHash();
    record = PredictionRecord(poolId, predictionId);
  }

  /// Returns paginated list of all prediction records across the system
  /// @param offset Starting index for pagination
  /// @param limit Maximum number of records to return
  /// @return records Array of prediction records
  function predictionRecordsPaginated(uint256 offset, uint256 limit)
    external
    view
    returns (PredictionRecord[] memory records)
  {
    records = _paginatePredictionRecordsArray(state.predictionRecordHashes, allStats().noOfPredictions, offset, limit);
  }

  /// Returns paginated list of winning prediction records
  /// @param offset Starting index for pagination
  /// @param limit Maximum number of records to return
  /// @return records Array of winner records
  function winnerRecordsPaginated(uint256 offset, uint256 limit)
    external
    view
    returns (PredictionRecord[] memory records)
  {
    records = _paginatePredictionRecordsArray(state.winnerRecordHashes, allStats().noOfWinnings, offset, limit);
  }

  /// Returns paginated list of claimed winning prediction records
  /// @param offset Starting index for pagination
  /// @param limit Maximum number of records to return
  /// @return records Array of claimed winner records
  function claimedRecordsPaginated(uint256 offset, uint256 limit)
    external
    view
    returns (PredictionRecord[] memory records)
  {
    records = _paginatePredictionRecordsArray(state.claimedRecordHashes, allStats().noOfClaimedWinnings, offset, limit);
  }

  /// Returns comprehensive statistics for a specific user
  /// @param user Address of the user to get statistics for
  /// @return stats User's prediction and pool statistics
  function userStats(address user) public view returns (UserPredictionStats memory stats) {
    if (user == address(0)) revert InvalidAddress();
    (uint256 a, uint256 b, uint256 c, uint256 d, uint256 e, uint256 f, uint256 g, uint256 h) = state.userStats(user);
    stats = UserPredictionStats(a, b, c, d, e, f, g, h);
  }

  /// Returns multiple user stats for the provided list of users
  /// @param users Array of user addresses
  /// @return statsList Array of UserPredictionStats corresponding to each user
  function usersStatsBulk(address[] calldata users) external view returns (UserPredictionStats[] memory statsList) {
    statsList = new UserPredictionStats[](users.length);
    for (uint256 i = 0; i < users.length; i += 1) {
      statsList[i] = userStats(users[i]);
    }
  }

  /// Returns paginated list of pool IDs that a user has joined
  /// @param user Address of the user
  /// @param offset Starting index for pagination
  /// @param limit Maximum number of pool IDs to return
  /// @return poolIds Array of pool IDs the user has participated in
  function userJoinedPoolIdsPaginated(address user, uint256 offset, uint256 limit)
    external
    view
    returns (uint256[] memory poolIds)
  {
    if (user == address(0)) revert InvalidAddress();

    uint256 total = userStats(user).noOfJoinedPools;
    if (offset >= total) return new uint256[](0);

    uint256 end = offset + limit > total ? total : offset + limit;
    uint256 length = end - offset;
    poolIds = new uint256[](length);

    for (uint256 i = 0; i < length; i += 1) {
      poolIds[i] = state.joinedPoolIdsByAddresses(user, offset + i);
    }
  }

  /// Returns paginated list of prediction records for a specific user
  /// @param user Address of the user
  /// @param offset Starting index for pagination
  /// @param limit Maximum number of records to return
  /// @return records Array of the user's prediction record
  function userPredictionRecordsPaginated(address user, uint256 offset, uint256 limit)
    external
    view
    returns (PredictionRecord[] memory records)
  {
    if (user == address(0)) revert InvalidAddress();
    records = _paginateUserPredictionRecordsArray(
      state.userPredictionRecords, user, userStats(user).noOfPredictions, offset, limit
    );
  }

  /// Returns paginated list of winning record for a specific user
  /// @param user Address of the user
  /// @param offset Starting index for pagination
  /// @param limit Maximum number of records to return
  /// @return records Array of the user's winning records
  function userWinnerRecordsPaginated(address user, uint256 offset, uint256 limit)
    external
    view
    returns (PredictionRecord[] memory records)
  {
    if (user == address(0)) revert InvalidAddress();
    records = _paginateUserPredictionRecordsArray(
      state.winnerRecordHashesByAddresses, user, userStats(user).noOfWinnings, offset, limit
    );
  }

  /// Returns paginated list of claimable records for a specific user
  /// @param user Address of the user
  /// @param offset Starting index for pagination
  /// @param limit Maximum number of records to return
  /// @return records Array of the user's claimable records
  function userClaimableRecordsPaginated(address user, uint256 offset, uint256 limit)
    external
    view
    returns (PredictionRecord[] memory records)
  {
    if (user == address(0)) revert InvalidAddress();
    records = _paginateUserPredictionRecordsArray(
      state.claimableRecordHashesByAddresses, user, userStats(user).noOfClaimableWinnings, offset, limit
    );
  }

  /// Retrieves pool data by ID
  /// @param poolId ID of the pool to retrieve
  /// @return pool Complete pool data structure
  function pool(uint256 poolId) public view returns (Pool memory) {
    if (poolId == 0 || poolId > allStats().noOfPools) revert InvalidPoolId();
    (
      uint256 a,
      PoolSeeds memory b,
      bytes32 c,
      uint256 d,
      uint256 e,
      uint256 f,
      uint256 g,
      uint256 h,
      uint256 i,
      uint256 j
    ) = state.pools(poolId);
    return Pool(a, b, c, d, e, f, g, h, i, j);
  }

  /// Retrieves prediction data by pool and prediction ID
  /// @param poolId ID of the pool containing the prediction
  /// @param predictionId ID of the specific prediction
  /// @return prediction Complete prediction data structure
  function prediction(uint256 poolId, uint256 predictionId) external view returns (Prediction memory) {
    if (poolId == 0 || poolId > allStats().noOfPools) revert InvalidPoolId();
    Pool memory pool_ = pool(poolId);
    if (predictionId == 0 || predictionId > pool_.noOfPredictions) {
      revert InvalidPredictionId();
    }
    (address a, uint256 b, uint256 c, uint256 d, uint256 e, uint256 f, bool g) = state.predictions(poolId, predictionId);
    return Prediction(a, b, c, d, e, f, g);
  }

  /// Retrieves multiple pools by their IDs
  /// @param poolIds Array of pool IDs to fetch
  /// @return poolsList Array of Pool structs corresponding to the IDs
  function pools(uint256[] calldata poolIds) external view returns (Pool[] memory poolsList) {
    poolsList = new Pool[](poolIds.length);
    for (uint256 i = 0; i < poolIds.length; i += 1) {
      if (poolIds[i] == 0 || poolIds[i] > allStats().noOfPools) revert InvalidPoolId();
      (
        uint256 a,
        PoolSeeds memory b,
        bytes32 c,
        uint256 d,
        uint256 e,
        uint256 f,
        uint256 g,
        uint256 h,
        uint256 j,
        uint256 k
      ) = state.pools(poolIds[i]);
      poolsList[i] = Pool(a, b, c, d, e, f, g, h, j, k);
    }
  }

  /// Retrieves multiple predictions from a specific pool
  /// @param poolId ID of the pool to fetch predictions from
  /// @param predictionIds Array of prediction IDs to fetch
  /// @return predictionsList Array of Prediction structs
  function predictions(uint256 poolId, uint256[] calldata predictionIds)
    external
    view
    returns (Prediction[] memory predictionsList)
  {
    if (poolId == 0 || poolId > allStats().noOfPools) revert InvalidPoolId();
    Pool memory pool_ = pool(poolId);
    predictionsList = new Prediction[](predictionIds.length);
    for (uint256 i = 0; i < predictionIds.length; i += 1) {
      if (predictionIds[i] == 0 || predictionIds[i] > pool_.noOfPredictions) revert InvalidPredictionId();
      (address a, uint256 b, uint256 c, uint256 d, uint256 e, uint256 f, bool g) =
        state.predictions(poolId, predictionIds[i]);
      predictionsList[i] = Prediction(a, b, c, d, e, f, g);
    }
  }

  /// Returns user statistics within a specific pool
  /// @param poolId ID of the pool
  /// @param user Address of the user
  /// @return stats User's statistics for the specified pool
  function userInPoolPredictionStats(uint256 poolId, address user)
    public
    view
    returns (UserInPoolPredictionStats memory stats)
  {
    if (poolId == 0 || poolId > allStats().noOfPools) revert InvalidPoolId();
    if (user == address(0)) revert InvalidAddress();
    (uint256 a, uint256 b, uint256 c, uint256 d) = state.userInPoolPredictionStats(poolId, user);
    stats = UserInPoolPredictionStats(a, b, c, d);
  }

  /// Returns paginated list of prediction IDs made by a user in a specific pool
  /// @param poolId ID of the pool to fetch predictions from
  /// @param user Address of the user
  /// @param offset Starting index for pagination
  /// @param limit Maximum number of prediction IDs to return
  /// @return predictionIds Array of prediction IDs made by the user
  function userInPoolPredictionIdsPaginated(uint256 poolId, address user, uint256 offset, uint256 limit)
    external
    view
    returns (uint256[] memory predictionIds)
  {
    if (poolId == 0 || poolId > allStats().noOfPools) revert InvalidPoolId();
    if (user == address(0)) revert InvalidAddress();
    predictionIds = _paginateUint256ArrayForUserInPool(
      state.predictionIdsByAddressesPerPool,
      poolId,
      user,
      userInPoolPredictionStats(poolId, user).noOfPredictions,
      offset,
      limit
    );
  }

  /// Returns paginated list of winning prediction IDs for a user in a specific pool
  /// @param poolId ID of the pool to fetch from
  /// @param user Address of the user
  /// @param offset Starting index for pagination
  /// @param limit Maximum number of prediction IDs to return
  /// @return predictionIds Array of winning prediction IDs
  function userInPoolWinnerPredictionIdsPaginated(uint256 poolId, address user, uint256 offset, uint256 limit)
    external
    view
    returns (uint256[] memory predictionIds)
  {
    if (poolId == 0 || poolId > allStats().noOfPools) revert InvalidPoolId();
    if (user == address(0)) revert InvalidAddress();
    predictionIds = _paginateUint256ArrayForUserInPool(
      state.winnerPredictionIdsByAddressesPerPool,
      poolId,
      user,
      userInPoolPredictionStats(poolId, user).noOfWinnings,
      offset,
      limit
    );
  }

  /// Returns paginated list of claimable winning prediction IDs for a user in a specific pool
  /// @param poolId ID of the pool to fetch from
  /// @param user Address of the user
  /// @param offset Starting index for pagination
  /// @param limit Maximum number of prediction IDs to return
  /// @return predictionIds Array of claimable winning prediction IDs
  function userInPoolClaimablePredictionIdsPaginated(uint256 poolId, address user, uint256 offset, uint256 limit)
    external
    view
    returns (uint256[] memory predictionIds)
  {
    if (poolId == 0 || poolId > allStats().noOfPools) revert InvalidPoolId();
    if (user == address(0)) revert InvalidAddress();
    predictionIds = _paginateUint256ArrayForUserInPool(
      state.claimablePredictionIdsByAddressesPerPool,
      poolId,
      user,
      userInPoolPredictionStats(poolId, user).noOfClaimableWinnings,
      offset,
      limit
    );
  }

  /// Returns global statistics for a prediction token
  /// @param token Address of the prediction token
  /// @return details Token usage statistics across all pools
  function predictionTokenDetails(address token) external view returns (PredictionTokenDetails memory details) {
    if (token == address(0)) revert InvalidAddress();
    (uint256 a, uint256 b) = state.predictionTokenDetails(token);
    details = PredictionTokenDetails(a, b);
  }

  /// Returns global statistics for a stake token
  /// @param token Address of the stake token
  /// @return details Token usage statistics across all pools and predictions
  function stakeTokenDetails(address token) external view returns (StakeTokenDetails memory details) {
    if (token == address(0)) revert InvalidAddress();
    (uint256 a, uint256 b, uint256 c, uint256 d, uint256 e, uint256 f, uint256 g, uint256 h, uint256 i) =
      state.stakeTokenDetails(token);
    details = StakeTokenDetails(a, b, c, d, e, f, g, h, i);
  }

  /// Returns paginated list of prediction tokens used by a specific user
  /// @param user Address of the user
  /// @param offset Starting index for pagination
  /// @param limit Maximum number of tokens to return
  /// @return tokensList Array of prediction token addresses used by the user
  function userPredictionTokensPaginated(address user, uint256 offset, uint256 limit)
    external
    view
    returns (address[] memory tokensList)
  {
    if (user == address(0)) revert InvalidAddress();
    tokensList = _paginateAddressArrayForUser(
      state.userPredictionTokens, user, userStats(user).noOfPredictionTokens, offset, limit
    );
  }

  /// Returns user-specific statistics for a prediction token
  /// @param user Address of the user
  /// @param token Address of the prediction token
  /// @return details User's usage statistics for the specific token
  function userPredictionTokenDetails(address user, address token)
    external
    view
    returns (PredictionTokenDetails memory details)
  {
    if (user == address(0) || token == address(0)) revert InvalidAddress();
    (uint256 a, uint256 b) = state.userPredictionTokenDetails(user, token);
    details = PredictionTokenDetails(a, b);
  }

  /// Returns paginated list of stake tokens used by a specific user
  /// @param user Address of the user
  /// @param offset Starting index for pagination
  /// @param limit Maximum number of tokens to return
  /// @return tokensList Array of stake token addresses used by the user
  function userStakeTokensPaginated(address user, uint256 offset, uint256 limit)
    external
    view
    returns (address[] memory tokensList)
  {
    if (user == address(0)) revert InvalidAddress();
    tokensList =
      _paginateAddressArrayForUser(state.userStakeTokens, user, userStats(user).noOfStakeTokens, offset, limit);
  }

  /// Returns user-specific statistics for a stake token
  /// @param user Address of the user
  /// @param token Address of the stake token
  /// @return details User's usage statistics for the specific token
  function userStakeTokenDetails(address user, address token) external view returns (StakeTokenDetails memory details) {
    if (user == address(0) || token == address(0)) revert InvalidAddress();
    (uint256 a, uint256 b, uint256 c, uint256 d, uint256 e, uint256 f, uint256 g, uint256 h, uint256 i) =
      state.userStakeTokenDetails(user, token);
    details = StakeTokenDetails(a, b, c, d, e, f, g, h, i);
  }
}

